import React, { Component, useState } from 'react';
import { Navigator, View, Item, Button, Text, StyleSheet,ImageBackground, TextInput, Image } from 'react-native';
import { white } from 'color-name';
import App from '../../App';
import { NavigationContainer } from '@react-navigation/native';
import firebaseUser from '../user-management/Firebase_Helper';


export const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    return (

        <View style={styles.container}>

            <Text style ={styles.signintxt}>
                Sign In
            </Text>

            <View  rounded style={styles.usernamecontainer}>
                <TextInput placeholder='Email' defaultValue = {email} style={styles.emailInput} />
            </View>

            <View rounded style={styles.passwordcontainer}>
                <TextInput placeholder='Password' defaultValue = {this.state.password} style={styles.passwordinput}  secureTextEntry= {true} />
             </View>

            <Button title = 'Forgot Password' hasText transparent />

            <Button title = 'Login' rounded style={styles.loginbtn}  
             onPress={() => {
                const firebase = new Firebase_Helper()
                firebase.signIn(this.state.email,this.state.password)
                if (firebase.errorMessage === 'none' ) {
                // Send to App.js 
                console.log(firebaseUser)
                }
                else{
                // Show error message
                console.log(firebase.errorMessage);
                }
            }}/>
            <Button title = 'Sign Up' hasText transparent style={styles.signupbtn} />


        </View>

    );

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
