import React, { useState } from "react";
import { View, TextInput, Button, Dimensions, StyleSheet } from "react-native";
import {signUp, errorMessage, userDocument, firebaseUser, uid} from '../user-management/Firebase_Helper';
import {Stack} from '../../App'
import { useNavigation, NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import * as firebase from 'firebase';


function SignUpScreen ({ navigation: { navigate } }) {
  const [firstName, setFirst] = useState('');
  const [lastName, setLast] = useState('');
  const [phoneNumber, setNumber] = useState('');
  const [email, setemail] = useState('');
  const [password, setPass] = useState('');

  function sendToUserFirebase () {
    signUp(firstName,lastName,phoneNumber,email,password)
    // addUserTest() * Test Data Works!
    navigate('Main')
  }


  return (
    <View style={styles.container}>
      <View style={styles.card}>
      <TextInput
        style={{height: 40}}
        placeholder="First Name"
        onChangeText={firstName => setFirst(firstName)}

      />

      <TextInput
        style={{height: 40}}
        placeholder="Last Name"
        onChangeText={lastName => setLast(lastName)}

      />
      <TextInput
        style={{height: 40}}
        placeholder="Phone Number"
        onChangeText={phoneNumber => setNumber(phoneNumber)}

      />
       <TextInput
        style={{height: 40}}
        placeholder="Email Address"
        onChangeText={email => setemail(email)}

      />
       <TextInput
        style={{height: 40}}
        placeholder="Password"
        onChangeText={password => setPass(password)}

      />
      <TextInput
        style={{height: 40}}
        placeholder="Confirm Password"  
      />
      <Button 
        title = 'Already Have An Account'
        onPress={() => navigate('Login')}
      /> 

      <Button 
        title = 'Sign Up'
        onPress={() => 
        sendToUserFirebase() 
        }
        />  
      </View>
    </View>
  )
};
export default SignUpScreen;

const styles = StyleSheet.create({
  container: {
      width: Dimensions.get('window').width,
      height: Dimensions.get('window').height,
      alignItems: "center",
      justifyContent: "center",
  },
  card: {
      alignSelf: "center",
      alignItems: "center",
      backgroundColor: "#ffffff",
      width: 300,
      height: 300,
      borderRadius: 20,
      padding: 10,
      shadowColor: '#000000',
      shadowOffset: {
          width: 0,
          height: 5
      },
      shadowRadius: 10,
      shadowOpacity: 1.0
  },

});
