import React, { Component, useState } from 'react';
import { Navigator, View, Item, Button, Text, StyleSheet,ImageBackground, TextInput, Image } from 'react-native';
import { white } from 'color-name';

// Initialize Firebase
const firebaseConfig = {
    apiKey: "AIzaSyC-68UPtqGgmasYZf1rgl0fcm_fOnnxsnE",
    authDomain: "mobileorderingapp-cfe7f.firebaseapp.com",
    databaseURL: "https://mobileorderingapp-cfe7f.firebaseio.com",
    storageBucket: "mobileorderingapp-cfe7f.appspot.com",
  };
const firebaseApp = firebase.initializeApp(firebaseConfig);

export default class Login extends Component {

    constructor(props) {
        super(props)
        this.state = {
            username: 'test',
            password: 'test',
        }

    }
        buttonClickListner = () => {

        if (this.state.username == "test" && this.state.username == 'test') {
            console.log(`Username is ${this.state.username}`);


        }
        else {
            console.log('Login Failed ');

        }

    }

  render() {

    return (

      <View style={styles.container}>
            {/* <ImageBackground source={require('../assets/cityimg.jpg')} style={styles.bgImage}> */}
            <Text style ={styles.signintxt}>
            Sign In
            </Text>

            <View  rounded style={styles.usernamecontainer}>
                <TextInput placeholder='Username' defaultValue = {this.state.username} style={styles.usernameinput} />
            </View>

            <View rounded style={styles.passwordcontainer}>
                <TextInput placeholder='Password' defaultValue = {this.state.password} style={styles.passwordinput}  secureTextEntry= {true} />
             </View>

            <Button title = 'Forgot Password' hasText transparent>
              <Text>Forgot Password</Text>
            </Button>

            <Button title = 'Login' rounded style={styles.loginbtn}  onPress={this.buttonClickListner}>

            </Button>

            <Button title = 'Sign Up' hasText transparent style={styles.signupbtn}>
            <Text>Sign Up</Text>
          </Button>

       {/* </ImageBackground> */}
      </View>

    );


  }


}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#717AF9',

    },
    loginbtn: {
        alignItems: 'center',
        // justifyContent: 'center',
        // alignSelf: 'center',
        justifyContent:"space-between",
    },
    signupbtn: {
        alignSelf: 'center',
        color: '#ffffff',
        justifyContent:"space-between",
    },
    signintxt: {
        paddingTop: 200,
        paddingBottom: 50,
        fontWeight: 'bold',
        fontSize: 18,
        alignSelf: 'center'
    },
    usernameinput: {



    },
    passwordinput: {



    },
    passwordcontainer: {
        alignSelf: "center",
        backgroundColor: '#ffffff',
        width: 300,
        justifyContent:"space-between",
        marginTop: 20,


    },
    usernamecontainer: {
        alignSelf: "center",
        backgroundColor: '#ffffff',
        width: 300,
        justifyContent:"space-between",
        flexDirection: "column",
        marginTop: 20,



    },
    bgImage: {
        flex: 1,
        resizeMode: "cover",
        justifyContent: "center"
    }

  });
