import React, { useState } from "react";
import { View, TextInput, Button } from "react-native";
import {signUp, errorMessage, addUserDocument, userDocument, firebaseUser, uid} from '../user-management/Firebase_Helper';
import {Stack} from '../../App'
import { useNavigation, NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import * as firebase from 'firebase';


function SignUpScreen ({ navigation: { navigate } }) {
  const [firstName, setFirst] = useState('');
  const [lastName, setLast] = useState('');
  const [phoneNumber, setNumber] = useState('');
  const [email, setemail] = useState('');
  const [password, setPass] = useState('');

  function sendToUserFirebase () {
    signUp(email,password)
    createUserDocument(uid,firstName,lastName,phoneNumber,email,password)
    // addUserTest() * Test Data Works!
    navigate('Main')
  }

  function createUserDocument (uid) {
    addUserDocument(firstName,lastName,phoneNumber,email,password)
  }

  return (
    <View>
      <TextInput
        style={{height: 40}}
        placeholder="First Name"
        onChangeText={firstName => setFirst(firstName)}

      />

      <TextInput
        style={{height: 40}}
        placeholder="Last Name"
        onChangeText={lastName => setLast(lastName)}

      />
      <TextInput
        style={{height: 40}}
        placeholder="Phone Number"
        onChangeText={phoneNumber => setNumber(phoneNumber)}

      />
       <TextInput
        style={{height: 40}}
        placeholder="Email Address"
        onChangeText={email => setemail(email)}

      />
       <TextInput
        style={{height: 40}}
        placeholder="Password"
        onChangeText={password => setPass(password)}

      />
      <TextInput
        style={{height: 40}}
        placeholder="Confirm Password"  
      />
      <Button 
        title = 'Already Have An Account'
        onPress={() => navigate('Login')}
      /> 

      <Button 
        title = 'Sign Up'
        onPress={() => 
        sendToUserFirebase() 
        }
        />  
    </View>
  )
};
export default SignUpScreen;
