import React, { useState } from 'react';
import { Navigator, View, Button, Text, StyleSheet, TextInput, Image, Dimensions, Platform } from 'react-native';
import { white } from 'color-name';
import App, { Stack } from '../../App';
import { signIn, errorMessage, sucessful, firebaseConfig } from '../user-management/Firebase_Helper';
import SignUpScreen from './SignUp';
import { auth } from 'firebase';
import { NavigationContainer, useNavigation } from '@react-navigation/native';
import { color } from 'react-native-reanimated';
import * as firebase from 'firebase';
import firestore from '@react-native-firebase/firestore';
import { render } from 'react-dom';

export default class LoginScreen extends React.Component {

    constructor(props) {
        super(props);
        state = {
            email: '',
            password: ''
        };  
    }

    componentDidMount() {
        const user = firebase.auth().currentUser;
    }

    clear = () => {
        this.textInputRef.clear();
    }
    render() {
        const { navigation: { navigate } } = this.props;

        return (
            <View style={styles.container}>
                <View style={styles.card}>
                    <Text style={styles.signintxt} title="Sign In">
                        Sign In
                    </Text>

                    <TextInput ref='emailRef' placeholder='Email' value={this.state.email} onChangeText={this.state.email} style={styles.emailInput} />

                    <TextInput ref='passwordRef' placeholder='Password' value={this.state.password} onChangeText={this.state.password} style={styles.passwordInput} secureTextEntry={true} />


                    {/* <Button title = 'Forgot Password' hasText transparent /> */}
                    <View style={styles.loginbtn}>
                        <Button title='Login' rounded style={styles.loginbtn}
                            onPress={() => {
                                firebase.auth().signInWithEmailAndPassword(this.emailInput,this.passwordInput).then(cred => {
                                    // var userDocument = firestore()
                                    //   .collection('Users')
                                    //   .doc(cred.user.uid);
                                    navigate("Main")
                                    reset()
                                })
                                    .catch(function (error) {
                                        // Handle Errors here
                                        console.log(error.message)

                                    });
                                reset()
                            }} />
                    </View>
                    <View style={styles.signupbtn}>
                        <Button title='Sign Up' hasText transparent style={styles.signupbtn} onPress={() => navigate('SignUp')} />
                    </View>
                </View>
            </View>

        );

    }


}

const styles = StyleSheet.create({
    container: {
        width: Dimensions.get('window').width,
        height: Dimensions.get('window').height,
        alignItems: "center",
        justifyContent: "center",
    },
    card: {
        alignSelf: "center",
        alignItems: "center",
        backgroundColor: "#ffffff",
        width: 300,
        height: 300,
        borderRadius: 20,
        padding: 10,
        shadowColor: '#000000',
        shadowOffset: {
            width: 0,
            height: 5
        },
        shadowRadius: 10,
        shadowOpacity: 1.0
    },
    loginbtn: {
        marginTop: 10,
        padding: 15,
        borderRadius: 50,

    },
    signupbtn: {
        marginTop: 10,

    },
    signintxt: {
        marginTop: 10,
        fontFamily: 'San Francisco',
        fontWeight: "bold",
        fontSize: 18,
        alignSelf: "center",
        marginBottom: 50,
    },
    emailInput: {


    },
    passwordInput: {
        marginTop: 10,
    },
});
