import React from 'react';
import * as firebase from 'firebase';
import App from '../../App';
// Initialize Firebase

const firebaseConfig = {
    apiKey: "AIzaSyC-68UPtqGgmasYZf1rgl0fcm_fOnnxsnE",
    authDomain: "mobileorderingapp-cfe7f.firebaseapp.com",
    databaseURL: "https://mobileorderingapp-cfe7f.firebaseio.com",
    storageBucket: "mobileorderingapp-cfe7f.appspot.com",
  };
const firebaseApp = firebase.initializeApp(firebaseConfig);
var message = '';
export default class FirebaseController extends React.Component{
    constructor() {

    this.state = {
        errorMessage: '',

    };

    this.signIn.bind(this);
    this.setErrorMessage.bind(this);


    }

    signIn(email,password) {

        firebase.auth().signInWithEmailAndPassword(email, password)
        .then(function() {
            console.log("Succuessful login")
        })
        .catch(function(error) {
            // Handle Errors here
            message = error.message;


        }); 
        this.state.errorMessage = message;
        this.setErrorMessage();
        console.log(this.state.errorMessage);
    }

    setErrorMessage = () => {
      this.setState({errorMessage: message}) ;  
    }


  }
