import React, { useState } from 'react';
import { Navigator, View, Button, Text, StyleSheet, TextInput, Image } from 'react-native';
import { white } from 'color-name';
import App, { Stack } from '../../App';
import { signIn, errorMessage, sucessful } from '../user-management/Firebase_Helper';
import SignUpScreen from './SignUp';
import { auth } from 'firebase';
import { NavigationContainer, useNavigation } from '@react-navigation/native';

function LoginScreen({ navigation: { navigate } }) {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    return (
        <View style={styles.container}>
            <View style={styles.card}>
                <Text style={styles.signintxt} title="Sign In">
                    Sign In
                </Text>


                <TextInput placeholder='Email' defaultValue={email} onChangeText={email => setEmail(email)} style={styles.emailInput} />

                <TextInput placeholder='Password' defaultValue={password} onChangeText={password => setPassword(password)} style={styles.passwordInput} secureTextEntry={true} />


                {/* <Button title = 'Forgot Password' hasText transparent /> */}
                <View style={styles.loginbtn}>
                    <Button title='Login' rounded style={styles.loginbtn}
                        onPress={() => {
                            signIn(email, password)
                            if (sucessful == true) {
                                navigate("Main")
                            }
                            else {
                                console.log(errorMessage);
                            }



                        }} />
                </View>
                <View style={styles.signupbtn}>
                    <Button title='Sign Up' hasText transparent style={styles.signupbtn} onPress={() => navigate('SignUp')} />
                </View>
                <Text style={styles.signintxt} >

                </Text>
            </View>
        </View>

    );

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        width: 300,
        alignItems: "center",

    },
    card: {
        backgroundColor: '#ffffff',
        alignContent: 'center',
        alignSelf: 'center',
        width: 400,
        height: 200,
    },
    loginbtn: {
        alignSelf: 'center',
        marginTop: 20,

    },
    signupbtn: {
        alignSelf: 'center',
        justifyContent: "space-between",
        marginTop: 20,

    },
    signintxt: {
        paddingTop: 200,
        paddingBottom: 50,
        fontWeight: 'bold',
        fontSize: 18,
        alignSelf: 'center',
        justifyContent: "space-between",
        marginTop: 20,
    },
    emailInput: {
        alignSelf: "center",
        width: 300,
        justifyContent: "space-between",
        marginTop: 20,

    },
    passwordInput: {
        alignSelf: "center",
        width: 300,
        justifyContent: "space-between",
        marginTop: 20,
    }
});

export default LoginScreen;
