    lastName: lastName
  })
}
export function setPhoneNumber (phoneNumber) {
  userDocument = firestore.collection('Users').doc(uid).set({
    phoneNumber: phoneNumber
  })
}
export function setEmail (email) {
  userDocument = firestore.collection('Users').doc(uid).set({
    email: email
  })
